public class nestedLoop {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i=0; i<7; i++) {
			System.out.println("--------");
			for (int j=0; j<5; j++) {
				System.out.println("*******");
			}
		}

	}

}
